mapper.select:
SELECT *
FROM MEMBER 
WHERE id = #{id} AND pw = #{pw}

mapper.insert:
INSERT INTO BLOG
(
	no, memberno
)
VALUES 
(
	blog_seq.nextval, #{memberno}
)

mapper.select:
SELECT no 
FROM BLOG 
WHERE memberno = #{memberno}

mapper.update:
UPDATE BLOG SET 
title = #{title}, proimg = #{proimg}, profile = #{profile}
WHERE memberno = #{memberno}

mapper.insert:
INSERT INTO POST 
(
	no, regdate, title, content, blogno, memberno, postaccess, topicno
)
VALUES 
(
	post_seq.nextval, sysdate, #{title}, #{content}, #{blogno}, #{memberno}, #{postaccess}, #{topicno}
)

mapper.select:
  SELECT *
	FROM (
          select rownum as rnum, no, regdate, title, content, blogno, memberno, postaccess, topicno 
          from POST
          WHERE blogno = #{blogno}
       ) 
  WHERE rnum >= #{sPage}+1 AND  rnum <= #{ePage}+#{sPage} 
  ORDER BY regdate

mapper.insert:
    	INSERT INTO CATEGORY 
		(
			no, name, blogno
		)
		VALUES 
		(
			category_seq.nextval, #{name}, #{blogno}
		)

mapper.update:
UPDATE CATEGORY 
SET 
name=#{name}
WHERE no = #{no}

mapper.select:
SELECT * 
FROM SUBCATEGORY 
WHERE blogno = #{blogno} AND categoryno = #{categoryno}

mapper.insert:
    	INSERT INTO SUBCATEGORY 
		(
			no, name, categoryno, blogno
		)
		VALUES 
		(
			subcategory_seq.nextval, #{name}, #{categoryno}, #{blogno}
		)

mapper.update:
UPDATE SUBCATEGORY 
SET 
name=#{name}
WHERE no = #{no} AND blogno = #{blogno} AND categoryno = #{categoryno}
